import { IonImg, IonText } from '@ionic/react';
import { logoPills, IconCloseCircle } from 'assets';
import React from 'react';

import './styles.css';

interface MedicationProps {
  name: any;
  quantity: any;
  period: any;
  dosage: any;
}

const Medication = (name: any, quantity: any, period: any, dosage: any) => {
  return (
    <div className="medication-container">
      <IonImg src={logoPills}></IonImg>
      <div className="medication-texts">
        <IonText class="medication-texts-title">`${name}`</IonText>
        <IonText class="medication-texts-subtitle">
          `${quantity}` vezes ao `${period}` `${dosage + "mg"}`
        </IonText>
      </div>
      <IconCloseCircle color="#c9c7c9" />
    </div>
  );
};
export default Medication;
