/* eslint-disable react/require-default-props */
import React from 'react';


import { SchoolOptionValue } from 'models/enrollment';

import {
  SelectContainer,
  StyledSelect,
  Label,
  Message,
  RowContainer,
} from './styles';

export type OptionValuesTypes =
  | string
  | boolean
  | number
  | SchoolOptionValue
  | undefined;

export interface Option {
  value: OptionValuesTypes;
  label: string;
}

export interface SelectProps {
  options: Option[];
  placeholder?: string;
  label?: string;
  width?: string;
  height?: string;
  name?: string;
  defaultValue?: Option | null;
  value?: Option | Option[] | null;
  onChange?: (inputValue: any, actionMeta: any) => void;
  hint?: string;
  messageError?: string;
  disabled?: boolean;
  info?: string;
  isMulti?: boolean;
}

const Select: React.FC<SelectProps> = ({
  options,
  placeholder = 'Selecione uma opção',
  width = '100%',
  height = '40px',
  label = '',
  onChange,
  defaultValue,
  name,
  value,
  hint = '',
  messageError = '',
  disabled = false,
  info = '',
  isMulti = false,
}) => {
  const error = !!messageError;
  return (
    <SelectContainer width={width} height={height}>
      {label && (
        <RowContainer>
          <Label>{label}</Label>
          {info}
        </RowContainer>
      )}
      <StyledSelect
        name={name}
        options={options}
        defaultValue={defaultValue}
        placeholder={placeholder}
        onChange={onChange}
        classNamePrefix="Select"
        value={value}
        error={error}
        isDisabled={disabled}
        noOptionsMessage={() => 'Sem opções'}
        isMulti={isMulti}
        menuPosition="fixed"
      />
      {!error ? (
        <Message error={error}>{hint}</Message>
      ) : (
        <Message error={error}>{messageError}</Message>
      )}
    </SelectContainer>
  );
};

export default Select;
