import { IonImg, IonText } from '@ionic/react';
import { logoPills, IconCloseCircle } from 'assets';
import MedicationModal from 'components/MedicationModal';
import { IMedication } from 'pages/Home';
import React, { useState } from 'react';

import './styles.css';

interface MedicationProps {
  medication: IMedication;
  deleteMedication(medicationToBeDeleted: string): void;
}

const Medication = ({ medication, deleteMedication }: MedicationProps) => {
  const [showModal, setShowModal] = useState(false);
  const [showYesModal, setShowYesModal] = useState(false);
  const [showNoModal, setShowNoModal] = useState(false);
  const [showSuggestionModal, setShowSuggestionModal] = useState(false);
  const [showSuggestionFeedbackModal, setShowSuggestionFeedbackModal] =
    useState(false);

  return (
    <div className="medication-container">
      <IonImg src={logoPills}></IonImg>
      <div
        className="medication-texts"
        onClick={() => {
          <MedicationModal
            show={true}
            setShow={setShowModal}
            showYes={showYesModal}
            setShowYes={setShowYesModal}
            showNo={showNoModal}
            setShowNo={setShowNoModal}
            showSuggestionModal={showSuggestionModal}
            setShowSuggestionModal={setShowSuggestionModal}
            showSuggestionFeedbackModal={showSuggestionFeedbackModal}
            setSuggestionFeedbackModal={setShowSuggestionFeedbackModal}
            isPlaying={false}
          />;
        }}
      >
        <IonText class="medication-texts-title">{medication.name}</IonText>
        <IonText class="medication-texts-subtitle">
          {`${medication.quantity}` +
            `${medication.dosage}` +
            'vezes ao' +
            `${medication.period}`}
        </IonText>
      </div>
      <button
        className="medication-close-button"
        type="button"
        onClick={() => deleteMedication(medication.name)}
      >
        <IconCloseCircle color="#c9c7c9" />
      </button>
    </div>
  );
};
export default Medication;
